mapper.select:
SELECT IDX as idx , title , COLOR as color , USERNAME , date_format(START_DATE, '%y/%m/%d %H:%i') as start_date , date_format(END_DATE, '%y/%m/%d %H:%i') as end_date , date_format(START_DATE, '%Y-%m-%dT%T') as start , date_format(END_DATE, '%Y-%m-%dT%T') as end , memo , done_flag , all_day as allDay FROM schedule
AND IDX = #{IDX}
AND USERNAME = #{username}

mapper.insert:

	SELECT IFNULL(MAX(IDX),0)+1 FROM schedule

INSERT INTO schedule
     ( IDX, START_DATE, END_DATE, TITLE, COLOR, MEMO, DONE_FLAG, USERNAME
     )
VALUES
     ( #{IDX}, #{START_DATE}, #{END_DATE}, #{TITLE}, #{COLOR}, #{MEMO}, 'N', #{username}
     )

mapper.update:
UPDATE schedule
   SET TITLE = #{TITLE}
     , MEMO = #{MEMO}
     , START_DATE = #{START_DATE}
     , END_DATE = #{END_DATE}
     , COLOR = #{COLOR}
 WHERE IDX = #{IDX}
   AND USERNAME = #{username}

mapper.delete:
DELETE FROM schedule
 WHERE IDX = #{IDX}
   AND USERNAME = #{username}
