mapper.select:
SELECT IDX, TITLE, CONTENTS, HITCNT, CREATE_DATETIME, CREATE_ID, UPDATE_DATETIME, UPDATE_ID
FROM board
WHERE IDX = #{IDX};

mapper.update:
UPDATE board SET
    TITLE = #{TITLE}
    , CONTENTS = #{CONTENTS}
    , UPDATE_ID = #{CREATE_ID}
    , UPDATE_DATETIME = NOW()
WHERE
    IDX = #{IDX}
