mapper.insert:
SELECT MAX(IDX)+1 FROM board
INSERT INTO board ( IDX, TITLE, CONTENTS, CREATE_ID, CREATE_DATETIME ) VALUES ( #{IDX}, #{TITLE}, #{CONTENTS}, 'testid', SYSDATE()
)

mapper.update:
UPDATE board
   SET TITLE = #{TITLE}
     , CONTENTS = #{CONTENTS}
     , UPDATE_ID = 'testid2'
     , UPDATE_DATETIME = SYSDATE()
 WHERE IDX = #{IDX}

UPDATE board
   SET HITCNT = IFNULL(HITCNT,0) + 1
 WHERE IDX = #{IDX}
